import React from "react";
import { useState } from "react";
import "../style/Task.css";

export const Task = ({ task, complete }) => {
    const [isChecked, setIsChecked] = useState(false);
    const checkboxHandle = (event) => {
        complete(task, complete);
        setIsChecked(!isChecked);
    }
  return (
    <div className="task" onClick={checkboxHandle}>
      <div className="task__text">
        <h5>{task.isComplete ? <s>{task.text}</s> : task.text}</h5>
      </div>
      <input type="checkbox" checked={isChecked} onChange={checkboxHandle}/>
    </div>
  );
};
